// src/components/Navbar.jsx
import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../contexts/UserContext";
import { logout as logoutAuth } from "../utils/auth";

function Navbar() {
  const navigate = useNavigate();
  const { user, setUser } = useContext(UserContext);

  if (user === undefined) {
    return (
      <nav className="p-4 bg-purple-200 text-center shadow-sm">
        <span className="italic text-gray-700">Chargement...</span>
      </nav>
    );
  }

  const handleLogout = () => {
    logoutAuth();
    setUser(null);
    navigate("/");
  };

  return (
    <nav className="flex flex-wrap justify-center gap-6 py-3 bg-purple-200 shadow-inner rounded-b-lg">
      <Link className="text-gray-800 hover:text-purple-800 font-medium transition" to="/">Accueil</Link>

      {user ? (
        <>
          <Link className="text-gray-800 hover:text-purple-800 font-medium transition" to="/profile">Mon profil</Link>
          <Link className="text-gray-800 hover:text-purple-800 font-medium transition" to="/places/new">Créer un lieu</Link>
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-600 transition font-medium"
          >
            Déconnexion
          </button>
        </>
      ) : (
        <>
          <Link className="text-gray-800 hover:text-purple-800 font-medium transition" to="/login">Se connecter</Link>
          <Link className="text-gray-800 hover:text-purple-800 font-medium transition" to="/register">S’inscrire</Link>
        </>
      )}
    </nav>
  );
}

export default Navbar;
