import React, { useContext } from "react";
import { UserContext } from "../contexts/UserContext";

function Header() {
  const { user } = useContext(UserContext);

  if (user === undefined) {
    return (
      <header className="p-4 bg-gradient-to-r from-red-400 via-pink-300 to-purple-400 shadow-md">
        <span className="italic text-white">Chargement...</span>
      </header>
    );
  }

  return (
    <header className="flex items-center justify-between px-6 py-4 bg-gradient-to-r from-red-400 via-pink-300 to-purple-400 shadow-lg rounded-b-2xl">
      {/* Logo */}
      <h1 className="text-3xl font-extrabold text-white tracking-wide">HBnB</h1>

      {/* Zone utilisateur */}
      {user ? (
        <div className="flex items-center gap-3">
          <img
            src={user.avatar ? `http://127.0.0.1:5000${user.avatar}` : "/default-avatar.png"}
            alt="avatar"
            className="w-12 h-12 rounded-full object-cover border-2 border-white shadow-md hover:border-yellow-300 transition"
          />
          <div className="flex flex-col text-white">
            <span className="text-sm opacity-90">Bonjour ðŸ‘‹</span>
            <span className="font-semibold">{user.username}</span>
            {user.is_admin && (
              <span className="text-xs bg-yellow-100 text-yellow-700 px-2 py-0.5 rounded-full mt-1">
                Admin
              </span>
            )}
          </div>
        </div>
