import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";

function LoginPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [message, setMessage] = useState("");

  // Gestion du reset intégré
  const [showReset, setShowReset] = useState(false);
  const [resetStep, setResetStep] = useState("request"); // "request" ou "reset"
  const [resetData, setResetData] = useState({
    email: "",
    code: "",
    newPassword: "",
    confirmPassword: ""
  });
  const [loading, setLoading] = useState(false);

  // --- Login ---
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      const response = await fetch("http://127.0.0.1:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("Un code de vérification a été envoyé à votre email.");
        localStorage.setItem("pendingEmail", formData.email);
        setTimeout(() => navigate("/verify"), 1500);
      } else {
        setMessage(data.message || "Email ou mot de passe incorrect.");
      }
    } catch (error) {
      console.error("Erreur :", error);
      setMessage("Impossible de contacter le serveur.");
    }
  };

  // --- Reset mot de passe ---
  const handleResetChange = (e) => {
    setResetData({ ...resetData, [e.target.name]: e.target.value });
  };

  // Étape 1 : envoi du code
  const handleForgotSubmit = async (e) => {
    e.preventDefault();
    if (!resetData.email) return setMessage("Email requis");
    try {
      setLoading(true);
      await API.post("/auth/forgot_password", { email: resetData.email });
      setMessage("Code envoyé à votre email");
      setResetStep("reset");
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.msg || "Erreur lors de la requête");
    } finally {
      setLoading(false);
    }
  };

  // Étape 2 : validation du code + mot de passe
  const handleResetSubmit = async (e) => {
    e.preventDefault();
    const { email, code, newPassword, confirmPassword } = resetData;
    if (!email || !code || !newPassword || !confirmPassword)
      return setMessage("Tous les champs sont requis");
    if (newPassword !== confirmPassword)
      return setMessage("Les mots de passe ne correspondent pas");

    try {
      setLoading(true);
      const res = await API.post("/auth/reset_password", {
        email,
        code,
        new_password: newPassword
      });
      setMessage(res.data.msg || "Mot de passe mis à jour avec succès");
      // reset formulaire et retour login
      setResetStep("request");
      setResetData({ email: "", code: "", newPassword: "", confirmPassword: "" });
      setShowReset(false);
    } catch (err) {
      console.error(err);
      setMessage(err.response?.data?.msg || "Erreur lors de la réinitialisation");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 shadow-md rounded-xl bg-white">
      {!showReset ? (
        <>
          <h2 className="text-2xl font-bold mb-4 text-center">Se connecter</h2>
          {message && <p className="mb-4 text-center text-red-500">{message}</p>}
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              placeholder="Adresse e-mail"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full p-2 border rounded"
            />
            <input
              type="password"
              name="password"
              placeholder="Mot de passe"
              value={formData.password}
              onChange={handleChange}
              required
              className="w-full p-2 border rounded"
            />
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
            >
              Se connecter
            </button>
          </form>
          <p className="mt-4 text-center">
            <span
              onClick={() => {
                setShowReset(true);
                setResetData({ email: formData.email, code: "", newPassword: "", confirmPassword: "" });
                setResetStep("request");
                setMessage("");
              }}
              className="text-blue-600 cursor-pointer"
            >
              Mot de passe oublié ?
            </span>
          </p>
          <p className="mt-2 text-center">
            Pas encore inscrit ?{" "}
            <span
              onClick={() => navigate("/register")}
              className="text-blue-600 cursor-pointer"
            >
              Créer un compte
            </span>
          </p>
        </>
      ) : (
        <form
          onSubmit={resetStep === "request" ? handleForgotSubmit : handleResetSubmit}
          style={{
            maxWidth: "400px",
            margin: "0 auto",
            padding: "1rem",
            border: "1px solid #ccc",
            borderRadius: "8px"
          }}
        >
          <h2 style={{ textAlign: "center" }}>
            {resetStep === "request" ? "Mot de passe oublié" : "Réinitialiser le mot de passe"}
          </h2>

          {message && <p style={{ color: "red", textAlign: "center" }}>{message}</p>}

          {resetStep === "request" && (
            <>
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={resetData.email}
                onChange={handleResetChange}
                required
              />
              <button
                type="submit"
                disabled={loading}
                style={{ marginTop: "1rem", width: "100%" }}
              >
                {loading ? "En cours..." : "Envoyer le code"}
              </button>
            </>
          )}

          {resetStep === "reset" && (
            <>
              <label>Code reçu par email</label>
              <input
                type="text"
                name="code"
                value={resetData.code}
                onChange={handleResetChange}
                placeholder="Entrez le code reçu"
                required
              />

              <label>Nouveau mot de passe</label>
              <input
                type="password"
                name="newPassword"
                value={resetData.newPassword}
                onChange={handleResetChange}
                required
              />

              <label>Confirmer mot de passe</label>
              <input
                type="password"
                name="confirmPassword"
                value={resetData.confirmPassword}
                onChange={handleResetChange}
                required
              />

              <button
                type="submit"
                disabled={loading}
                style={{ marginTop: "1rem", width: "100%" }}
              >
                {loading ? "En cours..." : "Réinitialiser le mot de passe"}
              </button>
            </>
          )}

          <button
            type="button"
            onClick={() => {
              setShowReset(false);
              setMessage("");
            }}
            style={{
              marginTop: "0.5rem",
              width: "100%",
              backgroundColor: "#ccc",
              color: "#000",
              padding: "0.5rem",
              borderRadius: "8px"
            }}
          >
            Retour à la connexion
          </button>
        </form>
      )}
    </div>
  );
}

export default LoginPage;
